import { createApp } from 'vue'
import App from './App.vue'
import router from './router'
import { createI18n } from 'vue-i18n'

// 国际化翻译文本
const messages = {
  zh: {
    common: {
      home: '首页',
      products: '产品中心',
      applications: '行业应用',
      services: '技术与支持',
      about: '关于我们'
    },
    nav: {
      agent: '招商代理',
      services: '服务中心'
    }
  },
  en: {
    common: {
      home: 'Home',
      products: 'Products',
      applications: 'Applications',
      services: 'Services',
      about: 'About Us'
    },
    nav: {
      agent: 'Global Partners',
      services: 'Service Center'
    }
  },
  fr: {
    common: {
      home: 'Accueil',
      products: 'Produits',
      applications: 'Applications',
      services: 'Services',
      about: 'À Propos'
    },
    nav: {
      agent: 'Partenaires',
      services: 'Centre de Service'
    }
  },
  es: {
    common: {
      home: 'Inicio',
      products: 'Productos',
      applications: 'Aplicaciones',
      services: 'Servicios',
      about: 'Nosotros'
    },
    nav: {
      agent: 'Socios Globales',
      services: 'Centro de Servicio'
    }
  }
}

// 创建i18n实例
const i18n = createI18n({
  legacy: true, // 使用传统API模式
  globalInjection: true, // 全局注册$t方法
  locale: localStorage.getItem('locale') || 'zh', // 默认语言
  fallbackLocale: 'zh', // 后备语言
  messages
})

// 引入Bootstrap样式和JS
import 'bootstrap/dist/css/bootstrap.css'
import 'bootstrap-icons/font/bootstrap-icons.css'
import 'bootstrap/dist/js/bootstrap.bundle.min.js'

// 创建Vue应用并挂载
const app = createApp(App)
app.use(router)
app.use(i18n)
app.mount('#app')
