import { createI18n } from 'vue-i18n';
import zh_CN from './locales/zh-CN.js';
import zh_TW from './locales/zh-TW.js';
import en from './locales/en.js';
import es from './locales/es.js';
import fr from './locales/fr.js';
import ar from './locales/ar.js';
import hi from './locales/hi.js';
import bn from './locales/bn.js';
import pt from './locales/pt.js';
import ru from './locales/ru.js';

const i18n = createI18n({
  legacy: false,
  locale: localStorage.getItem('language') || 'zh-CN', // 默认语言为中文简体
  fallbackLocale: 'en', // 如果当前语言没有对应的翻译，则使用英文作为备选
  messages: {
    'zh-CN': zh_CN,
    'zh-TW': zh_TW,
    'en': en,
    'es': es,
    'fr': fr,
    'ar': ar,
    'hi': hi,
    'bn': bn,
    'pt': pt,
    'ru': ru
  },
  globalInjection: true
});

export default i18n;

// 语言选项数组，用于语言切换组件
export const languageOptions = [
  { value: 'zh-CN', label: '中文简体', icon: '🇨🇳' },
  { value: 'zh-TW', label: '中文繁体', icon: '🇹🇼' },
  { value: 'en', label: 'English', icon: '🇺🇸' },
  { value: 'es', label: 'Español', icon: '🇪🇸' },
  { value: 'fr', label: 'Français', icon: '🇫🇷' },
  { value: 'ar', label: 'العربية', icon: '🇸🇦' },
  { value: 'hi', label: 'हिन्दी', icon: '🇮🇳' },
  { value: 'bn', label: 'বাংলা', icon: '🇧🇩' },
  { value: 'pt', label: 'Português', icon: '🇧🇷' },
  { value: 'ru', label: 'Русский', icon: '🇷🇺' }
];
