import { createApp } from 'vue'
import App from './App.vue'
import router from './router'
import i18n, { setLocale } from './i18n'

// 引入Bootstrap样式和JS
import 'bootstrap/dist/css/bootstrap.css'
import 'bootstrap-icons/font/bootstrap-icons.css'
import 'bootstrap/dist/js/bootstrap.bundle.min.js'

// 获取默认语言，如果没有则使用中文简体
const DEFAULT_LOCALE = 'zh-CN'
const savedLocale = localStorage.getItem('locale') || DEFAULT_LOCALE

// 强制设置默认语言
if (!localStorage.getItem('locale')) {
  localStorage.setItem('locale', DEFAULT_LOCALE)
}

// 应用加载前设置语言
document.documentElement.setAttribute('lang', savedLocale)
if (savedLocale === 'ar') {
  document.documentElement.setAttribute('dir', 'rtl')
  document.body.classList.add('rtl')
} else {
  document.documentElement.setAttribute('dir', 'ltr')
  document.body.classList.remove('rtl')
}

// 创建Vue应用
const app = createApp(App)

// 使用插件
app.use(router)
app.use(i18n)

// 挂载应用
app.mount('#app')

// 当DOM内容加载完成后，确保语言设置正确
document.addEventListener('DOMContentLoaded', () => {
  // 检查当前的locale是否和i18n实例中的一致
  const currentLocale = localStorage.getItem('locale') || DEFAULT_LOCALE
  if (i18n.global.locale !== currentLocale) {
    setLocale(currentLocale)
  }

  console.log('App initialized with language:', i18n.global.locale)
})

// 监听语言变化事件
window.addEventListener('languageChanged', () => {
  // 这里可以执行语言切换后的额外逻辑
  console.log('Language changed to:', i18n.global.locale.value)
})
