      {
        path: 'system/:id',
        name: 'System',
        component: () => import('../views/products/details/System.vue')
      },
      {
        path: 'support/:id',
        name: 'ProductSupport',
        component: () => import('../views/products/details/Support.vue')
      },
      {
        path: 'drone/fpv',
        name: 'FPVDrone',
        component: () => import('../views/products/drone/FPV.vue')
      },
      {
        path: 'drone/multi-rotor',
        name: 'MultiRotorDrone',
